export default function counterReducer(pre = 0,action){
    // const type = action.type
    const {data} =action
    switch (type) {
        case 'increment':
            console.log('+')
            return pre+data

        case 'decrement':
            console.log('+')
            return pre-data
        default:
            return pre

    }


}
